local plugin = {
        ["PluginName"] = "Advanced ESP",
            ["PluginDescription"] = "ESP avec couleurs d'équipe et noms",
                ["Commands"] = {
                        ["myesp"] = {
                                    ["ListName"] = "myesp / unesp",
                                                ["Description"] = "Active l'ESP (Rouge = Ennemi, Vert = Ami)",
                                                            ["Aliases"] = {"esp"},
                                                                        ["Function"] = function(args, speaker)
                                                                                        _G.ESP_Enabled = true
                                                                                                        
                                                                                                                        local function applyESP(player)
                                                                                                                                            if player == game.Players.LocalPlayer then return end
                                                                                                                                                                
                                                                                                                                                                                    player.CharacterAdded:Connect(function(char)
                                                                                                                                                                                                            if _G.ESP_Enabled then
                                                                                                                                                                                                                                        task.wait(0.5)
                                                                                                                                                                                                                                                                    createESP(player)
                                                                                                                                                                                                                                                                                            end
                                                                                                                                                                                                                                                                                                                end)
                                                                                                                                                                                                                                                                                                                                    
                                                                                                                                                                                                                                                                                                                                                        if player.Character then
                                                                                                                                                                                                                                                                                                                                                                                createESP(player)
                                                                                                                                                                                                                                                                                                                                                                                                    end
                                                                                                                                                                                                                                                                                                                                                                                                                    end

                                                                                                                                                                                                                                                                                                                                                                                                                                    function createESP(player)
                                                                                                                                                                                                                                                                                                                                                                                                                                                        if not player.Character or not player.Character:FindFirstChild("HumanoidRootPart") then return end
                                                                                                                                                                                                                                                                                                                                                                                                                                                                            
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                -- Supprime l'ancien ESP s'il existe
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                    if player.Character:FindFirstChild("ESPHighlight") then player.Character.ESPHighlight:Destroy() end
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                        if player.Character:FindFirstChild("ESPName") then player.Character.ESPName:Destroy() end

                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                            -- Couleur selon l'équipe
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                local color = (player.Team == game.Players.LocalPlayer.Team) and Color3.fromRGB(0, 255, 0) or Color3.fromRGB(255, 0, 0)
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                    if player.Team == nil then color = Color3.fromRGB(255, 255, 255) end

                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                        -- Création du Highlight (Contour)
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                            local highlight = Instance.new("Highlight")
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                highlight.Name = "ESPHighlight"
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                    highlight.Parent = player.Character
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                        highlight.FillColor = color
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                            highlight.FillTransparency = 0.5
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                highlight.OutlineColor = Color3.fromRGB(255, 255, 255)

                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                    -- Création du Nom (BillboardGui)
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                        local bill = Instance.new("BillboardGui")
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                            bill.Name = "ESPName"
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                bill.AlwaysOnTop = true
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                    bill.Size = UDim2.new(0, 100, 0, 50)
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                        bill.Adornee = player.Character:FindFirstChild("Head")
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                            bill.StudsOffset = Vector3.new(0, 3, 0)
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                bill.Parent = player.Character

                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                    local lbl = Instance.new("TextLabel")
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                        lbl.BackgroundTransparency = 1
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                            lbl.Text = player.Name
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                lbl.TextColor3 = color
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                    lbl.TextStrokeTransparency = 0
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                        lbl.Size = UDim2.new(1, 0, 1, 0)
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                            lbl.Font = Enum.Font.SourceSansBold
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                lbl.TextSize = 14
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                    lbl.Parent = bill
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                    end

                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                    for _, p in pairs(game.Players:GetPlayers()) do
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                        applyESP(p)
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                        end
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                    end
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                            },
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                    ["unesp"] = {
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                ["ListName"] = "unesp",
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                            ["Description"] = "Désactive l'ESP",
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                        ["Aliases"] = {"noesp"},
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                    ["Function"] = function(args, speaker)
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                    _G.ESP_Enabled = false
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                    for _, p in pairs(game.Players:GetPlayers()) do
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                        if p.Character then
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                if p.Character:FindFirstChild("ESPHighlight") then p.Character.ESPHighlight:Destroy() end
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                        if p.Character:FindFirstChild("ESPName") then p.Character.ESPName:Destroy() end
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                            end
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                            end
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                        end
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                }
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                    }
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                    }

                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                    return plugin
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                    
}